v1 (data, context) {
  // skip if already set
  if(context.cache.notFirstRun) return data.value;
  context.cache.notFirstRun = true;

  // new without name - set empty so it's still required
  if(!data.Name) return '';

  const hasQuery = data.Pipeline.length == 1;
  const hasList = data.UseForList;
  const hasContent = data.ContentTypeStaticName != '';
  const key = (hasContent || hasList ? 'c' : '') + (hasList ? 'l' : '') + (hasQuery ? 'q':'');
  if(key == '') return '-'; // default to No-Content if not set
  return key;
}

