v1(data) {
  const value = data.value;
  const separator = ':';
  const prefix = data.Target.replace('Field.Settings.', 'Setting ').replace('Field.Value', 'Value');
  const prefixLight = prefix + separator;
  const prefixFull = prefixLight + ' ';

  // Case 1: Empty or Whitespace only, use prefix
  if(value == '' || !/\S/.test(value)) return prefix;

  // Case 2: Already contains prefix, possibly with separator, no additional text
  if(value == prefix || value == prefixLight || value == prefixFull) return prefix;

  // Case 3: Start with prefix and has more
  if(value.startsWith(prefixFull)) return value; 

  // case 4: Start with prefix but separator is incomplete
  if(value.startsWith(prefixLight)) return value.replace(prefixLight, prefixFull);
  if(value.startsWith(prefix)) return value.replace(prefix, prefixFull); 

  // case 5: No prefix yet, either missing or from previous setting
  const separatorPos = value.indexOf(separator);
  if(separatorPos == -1) return prefix;

  return prefix + value.substring(separatorPos); 
}

