v1(data, context) {
  var hints = [];
  if(!data.VisibleInEditUI) hints.push('hidden');
  if(data.Disabled) hints.push('disabled');
  if(data.Required) hints.push('required');
  if(data.DisableTranslation) hints.push('no i18n');
  if(data.IsEphemeral) hints.push('no-save');
  var hint = hints.length ? ' (' + hints.join(', ') + ')' : '';
  return data.default + hint; 
}

